JOIN orders_products op on o.orders_id = op.orders_id
JOIN orders_products_customisations opc on op.orders_products_id = opc.orders_products_id
WHERE o.orders_id = 5178998
GROUP BY o.orders_id, op.orders_products_id, opc.custom_positions_id
HAVING app_count > 1;
