SELECT (
																					CASE
																						WHEN duplicate_of = 0 THEN (
																							SELECT GROUP_CONCAT(is_colour_duplicate)
																							FROM customisations
																							WHERE duplicate_of = 0
																							AND is_colour_duplicate > 0
																						)
																						WHEN customisation_id = 0 AND duplicate_of > 0 AND is_colour_duplicate > 0 THEN (
																							SELECT GROUP_CONCAT(c3.is_colour_duplicate)
																							FROM customisations c1, customisations c2, customisations c3
																							WHERE c1.customisation_id = 0
																							AND c1.is_colour_duplicate > 0
																							AND c2.customisation_id = c1.duplicate_of
																							AND c3.duplicate_of = c2.customisation_id
																							AND c3.is_colour_duplicate > 0
																						)
																						ELSE -1
																					END
																				) AS duplicate_of_customisation
																				FROM customisations
																				WHERE (
																					duplicate_of = 0
																					OR customisation_id = 0
																				)
																				HAVING duplicate_of_customisation > 0;
